function checkTopBarStatus(){if("closed"!==readCookie("topBarStatusNew")&&document.querySelector("#topbar")){let t=document.getElementById("topbar");t.style.maxHeight="50vh";let e=document.getElementById("topbar-close-btn");e.addEventListener("click",e=>{createCookie("topBarStatusNew","closed",30),t.style.maxHeight="0",checkTopBarStatus()})}}checkTopBarStatus();